<style>
    .account_card {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        margin: 0 0 20px;
    }
    .account_card_image {
        width: 70px;
        height: 70px;
        border-radius: 35px;
        overflow: hidden;
    }
    .account_card_image img {
        display: block;
    }
    .account_card_content {
        width: calc(100% - 70px);
        padding: 0 0 0 20px;
    }
    .account_card_name {
